import React, { useState, useEffect } from 'react'
import { useHistory, Link  } from 'react-router-dom'
import { Helmet } from 'react-helmet'
import axios from 'axios';
import './activity.css'


const Activity = (props) => {
  const [activityData, setActivityData] = useState([]);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());


  useEffect(() => {
    let isMounted = true; // Add this flag

    const fetchData = async () => {
      try {
        const response = await axios.get('/api/user-activity'); // Replace with your actual endpoint
        console.log('Response data:', response.data);
        if (isMounted) { // Check if the component is still mounted
          const processedData = processActivityData(response.data);
          setActivityData(processedData);
        }
      } catch (error) {
        console.error('Error fetching activity data:', error);
      }
    };

    fetchData();

    return () => {
      isMounted = false; // Set the flag to false when the component unmounts
    };
  }, []);

  const processActivityData = (data) => {
    // Create an object to hold the processed data
    const processedData = {};

    // Iterate over each data item
    data.forEach((item) => {
      // Extract the year and month from the date
      const date = new Date(item.date);
      const year = date.getFullYear();
      const month = date.toLocaleString('default', { month: 'short' });

      // Initialize the year object if it doesn't exist
      if (!processedData[year]) {
        processedData[year] = {
          year,
          months: Array(12).fill(0), // Initialize an array of 12 months with 0 contributions
          contributions: 0,
        };
      }

      // Increment the contribution count for the month
      const monthIndex = date.getMonth(); // Get the month index (0-11)
      processedData[year].months[monthIndex]++;
      processedData[year].contributions++;
    });

    // Convert the processed data object into an array
    return Object.values(processedData) || [];
  };

  const onDatePicked = (date) => {
    alert(date);
  };

  return (
    <div className="account-container">
      <Helmet>
        <title>My Activity - Clear Mind</title>
        <meta property="og:title" content="Account - Clear Mind" />
      </Helmet>
      <div className="account-hero">
        <div className="account-hero1">
          <div className="account-container1">
            <h1 className="log-in-hero-heading heading1">
              <Link to='/home' className="log-in-hero-heading-link">
                Clear Mind
              </Link>
            </h1>
          </div>
        </div>
      </div>
      <div id="calendar">
        <CalendarControls
          year={selectedYear}
          onPrevYear={() => setSelectedYear(selectedYear - 1)}
          onNextYear={() => setSelectedYear(selectedYear + 1)}
        />
        <Calendar
          year={selectedYear}
          onPickDate={onDatePicked}
        />
      </div>
    </div>
  )
}

export default Activity
