import React ,{useMemo} from 'react';
import { useTable } from 'react-table';
import './activity-table.css';


const ActivityTable = ({ data: rawData }) => {
  const data = rawData.flatMap(item => item)
  console.log(data)
  if (!data){
    return null;
  }  
  const columns = useMemo(()=>[
    {
      Header: 'Year',
      accessor: 'year',
      
    },
    {
      Header: 'Months',
      accessor: 'months',
      columns:[

      ],
      Cell: ({ row }) => (
        <div>
          {row.original.months.map((month, index) => (
            <span key={index}>
              {month}
              {index < row.original.months.length - 1 ? ', ' : ''}
            </span>
          ))}
        </div>
      ),
    },
    {
      Header: 'Contributions',
      accessor: 'contributions',
    },
  ],[])

  const tableInstance = useTable({ columns, data });

  return (
    <table {...tableInstance.getTableProps()}>
      <thead>
        {tableInstance.headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render('Header')}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...tableInstance.getTableBodyProps()}>
      {tableInstance.rows.map((row) => {
          tableInstance.prepareRow(row);
          return (
            <tr {...row.getRowProps()}>
              {row.cells.map((cell) => {
                return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>;
              })}
            </tr>
          );
        })}
      </tbody>
    </table>);
};

const data = [
  {
    year: 2024,
    months: ['Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec', 'Jan', 'Feb', 'Mar', 'Apr'],
    contributions: 3,
  },
];

const App = () => {
  return <ActivityTable data={data} />;
};

export default App;
