import React, { useState, useEffect } from 'react'

import { Helmet } from 'react-helmet'
import InfoAccount from '../components/info-account'
import RegisterForm from '../components/register-form'
import LogInForm from '../components/log-in-form'
import './log-in.css'

const LogIn = (props) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false)
  const [userData, setUserData] = useState(null)

  useEffect(() => {
    // Check if user is logged in
    const user = localStorage.getItem('user')
    if (user) {
      setIsLoggedIn(true)
      setUserData(JSON.parse(user))
    }
  }, [])

  const handleLogin = (userInfo) => {
    // Save user info to local storage or manage session
    localStorage.setItem('user', JSON.stringify(userInfo))
    setIsLoggedIn(true)
    setUserData(userInfo)
  }

  const handleLogout = () => {
    // Clear user from local storage or manage session
    localStorage.removeItem('user')
    setIsLoggedIn(false)
    setUserData(null)
  }
  return (
    <div className="log-in-container">
      <Helmet>
        <title>LogIn - Clear Mind</title>
        <meta property="og:title" content="LogIn - Clear Mind" />
      </Helmet>
      <div className="log-in-hero">
        <div className="log-in-hero1">
          <div className="log-in-container1">
            <h1 className="log-in-hero-heading heading1">Clear Mind</h1>
            <div className="log-in-container2">
            {isLoggedIn ? (
                <InfoAccount
                  // Pass the necessary props to InfoAccount component
                  heroSubHeading={userData.email}
                  heroSubHeading3={userData.surname}
                  heroSubHeading31={userData.name}
                  // ...other props
                />
              ) : (
                <LogInForm
                  rootClassName="log-in-form-root-class-name"
                  onLogin={handleLogin}
                />
              )}
            </div>
            <div className="log-in-btn-group">
              <button className="log-in-home-button button">Log In</button>
              <button className="log-in-hero-button2 button">Register â†’</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default LogIn
